{# CONFIGURE TRUNK INTERFACES #}
{# STIG ID: CISC-L2-000200 (CAT II) - The Cisco switch must have all trunk links enabled statically. #}
{# STIG ID: CISC-L2-000230 (CAT II) - The Cisco switch must have the default VLAN pruned from all trunk ports that do not require it. #}
{# STIG ID: CISC-L2-000260 (CAT II) - The Cisco switch must have the native VLAN assigned to an ID other than the default VLAN for all 802.1q trunk links. #}
{# STIG ID: CISC-L2-000210 (NOT CONFIGURED CAT II) - The Cisco switch must have all disabled switch ports assigned to an unused VLAN. #}

{# Configuring the native VLAN is optional since it's likely to already be configured. #}
{% for interface in __interface %}
{% if interface.interfaceType == "Physical" && interface.portMode == "trunk" or interface.interfaceType == "Virtual" && interface.portMode == "trunk" %}
interface {{ interface.portName }}
! @start-ignore-compliance
 switchport
! @end-ignore-compliance
 switchport mode trunk
 switchport nonegotiate
! @start-ignore-compliance
 switchport trunk allowed vlan remove 1
! @end-ignore-compliance
{% if trunk_native_vlan != "" %}
 switchport trunk native vlan {{ trunk_native_vlan }}
{% endif %}
{% endif %}
{% endfor %}

{# STIG ID: CISC-RT-000160 (CAT II) - The Cisco switch must be configured to have IP directed broadcast disabled on all interfaces. #}
{# STIG ID: CISC-RT-000190 (CAT II) - The Cisco switch must be configured to have Internet Control Message Protocol (ICMP) redirect messages disabled on all external interfaces. #}
{# ip redirects are being disabled, however this would only be a finding external interfaces #}
{# ONLY DISABLE IP UNREACHABLES ON EXTERNAL INTERFACES. DO NOT DISABLE THEM WITHIN A CAMPUS NETWORK AS IT WILL BREAK PTMTUD #}
{# STIG ID: CISC-RT-000170 (CAT II) - The Cisco switch must be configured to have Internet Control Message Protocol (ICMP) unreachable messages disabled on all external interfaces. NOT A FINDING ON INTERNAL INTERFACES #}
{% for interface in __interface %}
{% if interface.interfaceType == "Physical" && interface.portMode == "routed" %}

{% if "Bluetooth" in interface.portName %}
! @start-ignore-compliance
interface {{interface.portName}}
{#description Shutdown#}
 shutdown
! @end-ignore-compliance

{% else %}
interface {{ interface.portName }}
! @start-ignore-compliance
 no ip directed-broadcast
! @end-ignore-compliance
 no ip proxy-arp
 no ip redirect
{% endif %}
{% endif %}
{% endfor %}


{# CONFIGURE ACCESS INTERFACES #}
{# STIG ID: CISC-L2-000100 (CAT II) - The Cisco switch must have Bridge Protocol Data Unit (BPDU) Guard enabled on all user-facing or untrusted access switch ports. #}
{# STIG ID: CISC-L2-000120 (CAT II) - The Cisco switch must have Unknown Unicast Flood Blocking (UUFB) enabled. #}
{# STIG ID: CISC-L2-000140 (NOT ENABLED - CAT II) - The Cisco switch must have IP Source Guard enabled on all user-facing or untrusted access switch ports. #}
{# STIG ID: CISC-L2-000160 (CAT III) - The Cisco switch must have Storm Control configured on all host-facing switchports. #}
{# STIG ID: CISC-L2-000250 (CAT II) - The Cisco switch must have all user-facing or untrusted ports configured as access switch ports. #}
{# STIG ID: CISC-L2-000270 (CAT III) - The Cisco switch must not have any switchports assigned to the native VLAN. #}

{# convert speed to integer for storm control applies universal access port configuration #}
{% set Integer = 0 %}
{# Scrutinize all interfaces #}
{% for interface in __interface %}
{% if interface.speed %}
{% set intspeed = interface.speed|int %}
{% if interface.interfaceType == "Physical" && interface.portMode != "trunk" && interface.portMode != "routed" %}
interface {{ interface.portName }}
! @start-ignore-compliance
 switchport
! @end-ignore-compliance
 switchport mode access
 switchport nonegotiate
 switchport block unicast
 no logging event link-status
 no snmp trap link-status
 spanning-tree portfast
 spanning-tree bpdufilter enable
 spanning-tree bpduguard enable
 spanning-tree guard root
 ip dhcp snooping limit rate 5
{# STIG ID: CISC-L2-000270 -  enable if necessary
 {% if trunk_native_vlan != "" %}
 no switchport access vlan {{ trunk_native_vlan }}
 {% endif %}
#}

{# IP source Guard not applied due to potential negative impact to devices with static IP's. Implement with caution #}
{# The IP Source Guard feature depends on the entries in the DHCP snooping database or static IP-MAC-VLAN configuration commands to verify IP-to-MAC address bindings. #}
{# ip verify source #}

{# Apply sorm-control to Gig interfaces #}
{## NOTE - The below levels are not best practice and should be taylored to your environment ##}

{% if intspeed == 1000000 %}
! @start-ignore-compliance
 storm-control broadcast level bps 1000000000
 storm-control unicast level bps 1000000000
! @end-ignore-compliance
!

{# Apply sorm-control to TenGig interfaces #}
{% if intspeed == 10000000 %}
! @start-ignore-compliance
 storm-control broadcast level bps 1000000000
 storm-control unicast level bps 1000000000
! @end-ignore-compliance
!

{# Apply sorm-control to FortyGig interfaces #}
{% if intspeed == 40000000 %}
! @start-ignore-compliance
 storm-control broadcast level bps 4000000000
 storm-control unicast level bps 4000000000
! @end-ignore-compliance
!
{% endif %}
{% endif %}
{% endif %}
{% endif %}
{% endif %}
{% endfor %}

{# STIG ID: CISC-L2-000240 (CAT II) - The Cisco switch must not use the default VLAN for management traffic. #}
interface Vlan1
 shutdown

{# Configure the data and voice vlans on all acccess ports of the switch. #}
{# This will overwrite any current interface vlan configuration and is optional. #}

{% if data_vlan != "" %}
vlan {{ data_vlan }}
{% if data_vlan_name != "" %}
 name {{ data_vlan_name }}
{% endif %}
{% endif %}


{% if voice_vlan != "" %}
vlan {{ voice_vlan }}
{% if voice_vlan_name != "" %}
 name {{ voice_vlan_name }}
{% endif %}
{% endif %}

{% for interface in __interface %}
{% if interface.interfaceType == "Physical" && interface.portMode != "trunk" && interface.portMode != "routed" %}
{% if data_vlan != "" or voice_vlan != "" %}
interface {{ interface.portName }}
{% if data_vlan != "" %}
 switchport access vlan {{ data_vlan }}
{% endif %}
{% if voice_vlan != "" %}
 switchport voice vlan {{ voice_vlan }}
{% endif %}
{% endif %}
{% endif %}
{% endfor %}

{# Make sure the new vlans are added to any pruned trunks #}

{% for interface in __interface %}
{% if interface.interfaceType == "Physical" && interface.portMode == "trunk" or interface.interfaceType == "Virtual" && interface.portMode == "trunk" %}
{% if data_vlan != "" or voice_vlan != "" %}
interface {{ interface.portName }}
{% if data_vlan != "" %}
! @start-ignore-compliance
 switchport trunk allowed vlan add {{ data_vlan }}
! @end-ignore-compliance
{% endif %}
{% if voice_vlan != "" %}
! @start-ignore-compliance
 switchport trunk allowed vlan add {{ voice_vlan }}
! @end-ignore-compliance
{% endif %}
{% endif %}
{% endif %}
{% endfor %}

{# STIG ID: CISC-L2-000210 (CAT II) - The Cisco switch must have all disabled switch ports assigned to an unused VLAN. #}
{% if unusedVLAN != "" %}
! @start-ignore-compliance
vlan {{ unusedVLAN }}
 name UNUSED_VLAN
 shutdown
! @end-ignore-compliance
{% for interface in __interface %}
{% if interface.interfaceType == "Physical" && interface.portMode != "trunk" && interface.portMode != "routed" && interface.adminStatus == "DOWN" %}
! @start-ignore-compliance
interface {{ interface.portName }}
 switchport access vlan {{ unusedVLAN }}
! @end-ignore-compliance
{% endif %}
{% endfor %}
{% endif %}

{# STIG ID: CISC-L2-000130 - The Cisco switch must have DHCP snooping (CAT II) #}
{## for all user VLANs to validate DHCP messages from untrusted sources. ##}
{% if device == "ACCESS" %}
ip dhcp snooping vlan 1-4094
no ip dhcp snooping information option
{# DHCP snooping database disabled due to stacked switch behavior with diseparate file systems.  Documented in bug ID CSCvc49871 #}
{# ip dhcp snooping database flash:DHCP_SNOOPING_DB #}
ip dhcp snooping

{% endif %}


{% for interface in __interface %}
{% if device == "ACCESS" %}
{% if interface.interfaceType == "Physical" && interface.portMode == "trunk" or interface.interfaceType == "Virtual" && interface.portMode == "trunk" %}
interface {{ interface.portName }}
 ip dhcp snooping trust

{% endif %}
{% endif %}
{% endfor %}

{# Update your VTP version configuration as necessary #}
! @start-ignore-compliance
vtp version 2
! @end-ignore-compliance
vtp mode transparent

{#  STIG ID: CISC-L2-000030 (CAT II) - The Cisco switch must authenticate all VLAN Trunk Protocol (VTP) messages with a hash function using the most secured cryptographic algorithm available. #}

{% if vtp_password != "" %}
! @start-ignore-compliance
vtp password {{ vtp_password }}
! @end-ignore-compliance
{% endif %}

{# STIG ID: CISC-L2-000180 (CAT II) - The Cisco switch must implement Rapid Spanning Tree Protocol (STP) where #}
{# STIG ID: CISC-L2-000110 (CAT II) - The Cisco switch must have STP Loop Guard enabled. (CAT II) #}
{# STIG ID: CISC-L2-000100 (CAT II) - The Cisco switch must have BPDU Guard enabled on all user-facing or untrusted access switch ports. (CAT II) #}
spanning-tree mode rapid-pvst
spanning-tree loopguard default
{% if device == "ACCESS" %}
spanning-tree portfast default
spanning-tree portfast bpduguard default
errdisable recovery cause bpduguard
errdisable recovery cause psecure-violation
{% endif %}
errdisable recovery interval 1800

{# STIG ID: CISC-L2-000150 (NOT ENABLED - CAT II) - The Cisco switch must have Dynamic Address Resolution Protocol (ARP) Inspection (DAI) enabled on all user VLANs. #}
{# DAI depends on the entries in the DHCP snooping binding database to verify IP-to-MAC address bindings in incoming ARP requests and ARP responses. #}
{# DAI is not applied due to the potential negative impact to hosts with static IP's.  Implement with caution #}
{# ip arp inspection vlan x,x-x #}

{# STIG ID: CISC-L2-000170 (CAT III) - The Cisco switch must have IGMP or MLD Snooping configured on all VLANs. #}
{# Enabled by deefault.  This re-enables IGMP snooping in-case it was disabled. #}
! @start-ignore-compliance
ip igmp snooping
! @end-ignore-compliance

{# STIG ID: CISC-L2-000190 (CAT II) - The Cisco switch must enable Unidirectional Link Detection (UDLD) to protect against one-way connections. #}
udld enable

{# STIG ID: CISC-ND-000490 (CAT II) - The Cisco switch must be configured with only one local account to be used as the account of last resort in the event the authentication server is unavailable. #}
{# STIG ID: CISC-ND-000550 (CAT II) - The Cisco switch must be configured to enforce a minimum 15-character password length. #}
{# STIG ID: CISC-ND-000570 (CAT II) - The Cisco switch must be configured to enforce password complexity by requiring that at least one upper-case character be used. #}
{# STIG ID: CISC-ND-000580 (CAT II) - The Cisco switch must be configured to enforce password complexity by requiring that at least one lower-case character be used. #}
{# STIG ID: CISC-ND-000590 (CAT II) - The Cisco switch must be configured to enforce password complexity by requiring that at least one numeric character be used. #}
{# STIG ID: CISC-ND-000600 (CAT II) - The Cisco switch must be configured to enforce password complexity by requiring that at least one special character be used. #}
{# STIG ID: CISC-ND-000610 (CAT II) - The Cisco switch must be configured to require that when a password is changed, the characters are changed in at least eight of the positions within the password. #}


aaa common-criteria policy PW_POLICY
 min-length 15
 max-length 127
 numeric-count 1
 upper-case 1
 lower-case 1
 special-case 1
 char-changes 8

